active');		$(target).slideDown('fast');	}	$(target).mouseup(function(e){		e.preventDefault();		return false;	});	var platform = navigator.platform;  	if( platform == 'iPad'){		$(document).unbind('touchstart');				document.addEventListener('touchend', function(e) {			if(auto == 'true'){				$(target).slideUp('fast');				e.preventDefault();				return false;			}		}, false);	}	var userAgent = navigator.userAgent.toLowerCase();    var isIphone = (userAgent.indexOf('iphone') != -1) ? true : false;    if (isIphone) {		$('#body').unbind('mouseup');			$('#body').mouseup(function(e){			if(auto == 'true')				$(target).slideUp('fast');		});	}	else{		$(document).unbind('mouseup');			$(document).mouseup(function(e){			if(auto == 'true')				$(target).slideUp('fast');						});	}};</script>